func main() {

	fmt.Println(firstAnswer())
	fmt.Println(secondAnswer())

}

func firstAnswer() int {
	var sb strings.Builder
	sb.WriteString(input)

	sp := strings.Split(sb.String(), "\n")

	count := 0

	for lineIndex, line := range sp {
		for i, digit := range line {
			if digit == '@' {
				kol := fa(i, lineIndex, sp)
				if kol < 4 {
					sb.WriteRune('x')
					count++

					continue
				}
			}
			sb.WriteRune(digit)
		}
		sb.WriteRune('\n')
	}
	return count
}
func secondAnswer() int {
	sp := strings.Split(input, "\n")

	intMap := make([][]int, len(sp))
	for i, v := range sp {
		for _, d := range v {
			if d == '@' {
				intMap[i] = append(intMap[i], 1)
			} else {
				intMap[i] = append(intMap[i], 0)
			}
		}
	}

	count := 0

	fu := func() bool {
		for lineIndex, line := range intMap {
			for i, digit := range line {
				if digit == 1 {
					k := sa(i, lineIndex, intMap)
					if k < 4 {
						count++
						intMap[lineIndex][i] = 0
						return false
					}
				}
			}
		}
		return true
	}

	for !fu() {

	}

	return count
}

func fa(x, y int, pole []string) int {
	count := 0
	maxY := len(pole) - 1
	maxX := len(pole[0]) - 1
	_, _ = maxX, maxY

	if x != 0 && y != 0 {
		if pole[y-1][x-1] == '@' { // 1
			count++
		}
	}

	if x != 0 {
		if pole[y][x-1] == '@' { // 2
			count++
		}

		if y+1 <= maxY {
			if pole[y+1][x-1] == '@' { // 3
				count++
			}
		}
	}

	if y != 0 {
		if x+1 <= maxX {
			if pole[y-1][x+1] == '@' { //4
				count++
			}
		}

		if pole[y-1][x] == '@' { // 5
			count++
		}
	}

	if y+1 <= maxY {
		if pole[y+1][x] == '@' { // 6
			count++
		}

		if x+1 <= maxX {
			if pole[y+1][x+1] == '@' { //7
				count++
			}
		}

	}

	if x+1 <= maxX {
		if pole[y][x+1] == '@' { // 8
			count++
		}
	}

	return count
}

func sa(x, y int, pole [][]int) int {
	count := 0
	maxY := len(pole) - 1
	maxX := len(pole[0]) - 1
	_, _ = maxX, maxY

	if x != 0 && y != 0 {
		if pole[y-1][x-1] == 1 { // 1
			count++
		}
	}

	if x != 0 {
		if pole[y][x-1] == 1 { // 2
			count++
		}

		if y+1 <= maxY {
			if pole[y+1][x-1] == 1 { // 3
				count++
			}
		}
	}

	if y != 0 {
		if x+1 <= maxX {
			if pole[y-1][x+1] == 1 { //4
				count++
			}
		}

		if pole[y-1][x] == 1 { // 5
			count++
		}
	}

	if y+1 <= maxY {
		if pole[y+1][x] == 1 { // 6
			count++
		}

		if x+1 <= maxX {
			if pole[y+1][x+1] == 1 { //7
				count++
			}
		}

	}

	if x+1 <= maxX {
		if pole[y][x+1] == 1 { // 8
			count++
		}
	}

	return count
}
